section.programlisting:
import Prelude hiding (repeat) import Data.Conduit
-- | Never give any data eof :: Monad m => Source m a eof = Source { sourcePull = return Closed , sourceClose = return () }
-- | Always give the same value repeat :: Monad m => a -> Source m a repeat a = Source { sourcePull = return $ Open (repeat a) a , sourceClose = return () }

import Data.Conduit import Control.Monad.Trans.Resource
-- | Provide data from the list, one element at a time. sourceList :: Resource m => [a] -> Source m a sourceList list = Source { sourcePull = case list of [] -> return Closed -- no more data
-- This is where we store our state: by return a new -- Source with the rest of the list x:xs -> return $ Open (sourceList xs) x , sourceClose = return () }

import Data.Conduit import System.IO import Control.Monad.IO.Class (liftIO)
-- We never have to touch the release key directly, sinkIO automatically -- releases our resource as soon as we return IODone from our push function, -- or sinkClose is called. sinkFile :: ResourceIO m => FilePath -> Sink Char m () sinkFile fp = sinkIO (openFile fp WriteMode) hClose -- push: notice that we are given the handle and the input (\handle char -> do liftIO $ hPutChar handle char return IOProcessing) -- close: we're also given the handle, but we don't use it (\_handle -> return ())
-- And we'll count how many values were in the stream. count :: Resource m => Sink a m Int count = sinkState 0 -- The push function gets both the current state and the next input... (\state _ignored -> -- and it returns the new state return $ StateProcessing $ state + 1) -- The close function gets the final state and returns the output. (\state -> return state)

import Prelude hiding (map, concatMap) import Data.Conduit
-- A simple conduit that just passes on the data as-is. passThrough :: Monad m => Conduit input m input passThrough = Conduit { conduitPush = \input -> return $ Producing passThrough [input] , conduitClose = return [] }
-- map values in a stream map :: Monad m => (input -> output) -> Conduit input m output map f = Conduit { conduitPush = \input -> return $ Producing (map f) [f input] , conduitClose = return [] }
-- map and concatenate concatMap :: Monad m => (input -> [output]) -> Conduit input m output concatMap f = Conduit { conduitPush = \input -> return $ Producing (concatMap f) $ f input , conduitClose = return [] }

